sys_ui_page.client_script:
	function runGetPageInfoCommand(){
		return window.top.g_automate.getPageInfo();
	}

	//input type, isReadonly, isClickable
	var test_components = [
		['i_img'],
		['i_button'],
		['i_checkbox'],
		['i_color'],
		['i_date'],		
		['i_email'],
		['i_file'],
		['i_image'],
		['i_number'],
		['i_radio'],
		['i_range'],
		['i_password'],
		['i_search'],
		['i_tel'],
		['i_text'],
		['i_time'],
		['i_url'],
		['i_week'],
		['i_select'],
		['i_text_area'],
		['i_datetime_local']
	];



describe("Test the manadatory mugshot properties for each components", function() {
		for(var i=0; i<test_components.length; i++){
		var component_id = test_components[i][0];
		pageInfo = runGetPageInfoCommand();
		for (var key in pageInfo) {
			  if (pageInfo.hasOwnProperty(key)) {
				var mug_shot = pageInfo[key];
				console.log("mugshot id: " + mug_shot.id);
				console.log("component id: " + component_id);
				if (mug_shot.id === component_id){
				  console.log("==============they are same!==================");
				  assertMondatoryMugshotProperties(mug_shot);
				} 
			  }
	}	
	//	runIsReadonlyTests(input_type, expectedIsReadonly, "");
	}	
});

function assertMondatoryMugshotProperties(mugshot_of_component){
	var mandatory_properties = ["tag", "methods", "area", "type"];
	for(var i=0; i<mandatory_properties.length; i++){
		console.log("looping..........");
		it("Compoent: " + mugshot_of_component.id + " should have mondatory properties: " + mandatory_properties[i], function() {
			expect(mugshot_of_component.hasOwnProperty("tag")).toBe(true,   "can't find the mandatory property" + mandatory_properties[i] + " on this component!");
		});	
	}
}

function runTest(attribute, expected_attribute_value){	
		it("attrbute: " + attribute + " should have this value: " + expected_attribute_value, function() {
			expect(attribute).toBe(expected_attribute_value,   attrbute + "is expected to be have this value: " + expected_attribute_value + " but not! ");
		});
}
